import {Component, OnInit} from '@angular/core';

@Component({
  // selector: 'app-servers', .app-servers  class='app-servers'
  selector: '[app-servers]',
  // templateUrl: './servers.component.html',``
  // templateUrl: './servers.component.html'
  // template: '<app-server></app-server>',
  templateUrl: './servers.component.html',
  styleUrls: ['./servers.component.css']
})
export class ServersComponent implements OnInit {
  allowNewServer = false;
  serverCreationStatus='没有服务器可以用于创建';
  serverName='测试服务器';

  constructor() {
    setTimeout(()=>{
      this.allowNewServer = true;
    },2000)
  }

  onCreateServer(){
    this.serverCreationStatus = '服务器可以创建';
  }

  onUpdateServerName(event: any){
    console.log(event);
    this.serverName = (<HTMLInputElement>event.target).value;
  }

  ngOnInit() {
  }

}
<button class="btn btn-primary" [disabled]="!allowNewServer" (click)="onCreateServer()">添加服务器</button>
<p>{{serverCreationStatus}}</p>

<hr>

